import React from 'react';
import ReactDOM from 'react-dom'
import logo from './logo.svg';
import './App.css';
import { render } from '@testing-library/react';

function addQuestion(){
  ReactDOM.render(
    <div class='questionTemplate'>
      <form>
        <label>
          Question:<br/>
          <textarea type="text" rows='20' cols='70'/>
          <textarea type="text" name="name" rows='20' cols='70'/><br/>
          <input type="submit" value="Submit" />
        </label>
      </form>
    </div>, document.getElementById('question')
  )
}

function App() {
  state = {
    isAboutVisible: false,
  }

  return (
    <div className="Nav">
      <div className="Button-Container">
      <div className="Nav-Text About-Button">
        <h2 onClick={() => this.setState({ isAboutVisible: true }) }>About</h2>
      </div>
      </div>
      { this.state.isAboutVisible ? <About /> : null }
    </div>
    );
}

export default App;
